using System;
class ElectricityBillCalculator
{
    static void Main()
    {
        // Input units consumed
        double units = 865;
        // Rate per unit (explicit conversion)
        double rate;
        if (units <= 100)
        {
            rate = 1.5;
        }
        else if (units <= 200)
        {
            rate = 2.0;
        }
        else
        {
            rate = 3.0;
        }
        // Calculate bill amount
        double billAmount = units * rate;
        // Implicit conversion
        int roundedBillAmount = (int)billAmount;
        // Determine category
        string category = rate == 3.0 ? "High Usage" : (rate == 2.0 ? "Moderate Usage" : "Low Usage");
        // Nested if-else for bill analysis
        if (billAmount > 500)
        {
            Console.WriteLine("Warning: High electricity bill.");
        }
        else
        {
            Console.WriteLine("Electricity bill is within the normal range.");
        }
        Console.WriteLine("Usage Category: " + category);
        Console.WriteLine("Total Bill Amount: $" + roundedBillAmount);
    }
}

